// 3000 digits of pi
var pi = "";

var dark =  ["#1f77b4", "#ff7f0e", "#2ca02c", "#d62728", "#9467bd", "#8c564b", "#e377c2", "#7f7f7f", "#bcbd22", "#17becf"];
var light = ["#aec7e8", "#ffbb78", "#98df8a", "#ff9896", "#c5b0d5", "#c49c94", "#f7b6d2", "#c7c7c7", "#dbdb8d", "#9edae5",];

var albers = [d3.lab(35,35,6), d3.lab(49,37,3), d3.lab(53,-13,-35), d3.lab(58,16,44), d3.lab(76,-8,-58), d3.lab(36,-4,-1), d3.lab(54,58,37), d3.lab(61,-35,1), d3.lab(70,26,1), d3.lab(79,-18,69)];

var spectral = colorbrewer.Spectral[10];
var greys = colorbrewer.Greys[9];
greys.push("#000000");

var out = albers; // initial color
//var over = dark; // color on mouse over

// Use nds digits
var nds = 1000;
pi = pi.slice(0,nds);

var columns = 40,
	size = 30,
	w = columns*size + 50,
	h = (pi.length/columns)*size + 50;

function rand(min, max) {
	return Math.random()*(max-min)+min;
}

function mouseover(d) {
  d3.select(this)
  	.style("opacity", "0.0"); // change opacity to reveal number
 	//.style("fill", function(d) {return over[parseInt(d)];})
}

function mouseout(d) {
  d3.select(this)
	.style("fill", function(d) {return out[parseInt(d)];})
	.style("opacity", "1.0");
}

var svg = d3.select("body").append("svg")
	.attr("width", w)
	.attr("height", h);

// Shadow effect from http://bl.ocks.org/cpbotha/5200394
//-------------------------------------------------------
var defs = svg.append("defs");

var filter = defs.append("filter")
    .attr("id", "drop-shadow")
    .attr("height", "130%");

filter.append("feGaussianBlur")
    .attr("in", "SourceAlpha")
    .attr("stdDeviation", 3)
    .attr("result", "blur");

filter.append("feOffset")
    .attr("in", "blur")
    .attr("dx", 3)
    .attr("dy", 3)
    .attr("result", "offsetBlur");

var feMerge = filter.append("feMerge");

feMerge.append("feMergeNode")
    .attr("in", "offsetBlur")
feMerge.append("feMergeNode")
    .attr("in", "SourceGraphic");
//-------------------------------

var nums = svg.selectAll("text").data(pi);

nums.enter()
	.append("text") 	
	.attr("transform", translate)
	.attr("dy", 20)
	.attr("dx", 10)
    .text(function(d) { return d;});

var rects = svg.selectAll("rect").data(pi);

rects.enter()
	.append("rect")
	.attr("transform", translate)
	.attr("width", function(d,i) {return size*rand(0.7,1);})
	.attr("height", function(d,i) {return size*rand(0.7,1);})
	//.attr("title", function(d,i) {return "digit " + parseInt(i+1) + " is " + d;})
	.style("fill", function(d) {return out[parseInt(d)];})
	.style("filter", "url(#drop-shadow)")
	.on("mouseover", mouseover)
	.on("mouseout", mouseout);

function translate(d,i) {
	var r = Math.floor(i/columns),
		c = i%columns;
	return "translate(" + c*size + "," + r*size + ")";
}
